package org.keysupport.pki.validation.cache;

public class CertificateCacheException extends Exception {

	/**
	 * 
	 */
	private static final long serialVersionUID = 569198975620688377L;

	public CertificateCacheException() {
		super();
	}

	public CertificateCacheException(String message) {
		super(message);
	}

	public CertificateCacheException(Throwable cause) {
		super(cause);
	}

	public CertificateCacheException(String message, Throwable cause) {
		super(message, cause);
	}

}
